// Process the batch results from all input items
const batches = $input.all();
const allTransactions = [];

batches.forEach((batch, batchIndex) => {
  const batchJson = batch.json;
  if (batchJson.message?.content?.transactions) {
    batchJson.message.content.transactions.forEach(transaction => {
      allTransactions.push({
        ...transaction,
        id: `SBD-${transaction.messageId ? transaction.messageId.toUpperCase() : ''}`,
        batchNumber: batchJson.batchNumber,
        processed: true
      });
    });
  }
});

// Generate summary statistics with transfer flagging
const uniqueBanks = [...new Set(allTransactions.map(t => t.bank))];
const allTotals = {};
let expenseCount = 0;
let transferCount = 0;

// Calculate totals by currency, separating expenses and transfers
allTransactions.forEach(transaction => {
  const isTransfer = transaction.isInternalTransfer === true;
  const currency = transaction.currency || 'UNKNOWN';
  const amount = parseFloat(transaction.amount || 0);

  // Count transaction types
  if (isTransfer) {
    transferCount++;
  } else {
    expenseCount++;
  }

  // Track amounts by currency and type
  if (!allTotals[currency]) {
    allTotals[currency] = { expenses: 0, transfers: 0, total: 0 };
  }

  if (isTransfer) {
    allTotals[currency].transfers += amount;
  } else {
    allTotals[currency].expenses += amount;
  }
  allTotals[currency].total += amount;
});

// Format currency display with breakdown
const currencyDisplay = Object.entries(allTotals)
  .map(([currency, totals]) => {
    if (totals.transfers > 0) {
      return `${currency} ${totals.expenses.toLocaleString()} expenses + ${totals.transfers.toLocaleString()} transfers`;
    }
    return `${currency} ${totals.expenses.toLocaleString()}`;
  })
  .join(' | ');

// Create expense-only display for main summary
const expenseOnlyDisplay = Object.entries(allTotals)
  .filter(([currency, totals]) => totals.expenses > 0)
  .map(([currency, totals]) => `${currency} ${totals.expenses.toLocaleString()}`)
  .join(' + ');

console.log(`Processed ${allTransactions.length} transactions from all batches`);
console.log(`Expenses: ${expenseCount}, Transfers: ${transferCount}`);
console.log(`Currency breakdown: ${currencyDisplay}`);

// Add summary to first transaction for easy access
if (allTransactions.length > 0) {
  allTransactions[0].summary = {
    totalTransactions: allTransactions.length,
    expenseCount,
    transferCount,
    uniqueBanks: uniqueBanks.join(', '),
    currencyDisplay,
    expenseOnlyDisplay,
    allTotals
  };
}

return allTransactions;

// Process emails in batches for efficiency
