const emails = allItems.filter(item => !item.json.isStartMessage);

console.log(`Processing ${emails.length} emails in batches`);

if (emails.length === 0) {
  return [{ nothingToProcess: true, message: "No emails to process" }];
}

// Group emails into batches of 5 for AI processing
