const DATE_REGEX = /^\d{4}-\d{2}-\d{2}$/;
const TIME_REGEX = /^\d{2}:\d{2}$/;

const initialValue = $("Get Row Value").first().json;

const formData = $input.first().json.data;
const sourceData = {
  Amount: formData.Amount || initialValue.amount,
  Currency: formData.Currency || initialValue.currency,
  Date: formData.Date || initialValue.date,
  Time: formData.Time || initialValue.time,
  Merchant: formData.Merchant || initialValue.merchant,
  Bank: formData.Bank || initialValue.bank,
};
const errors = [];
const warnings = [];
const updates = {};

// Helper function to check if field has value
const hasValue = (value) =>
  value !== null && value !== undefined && value.toString().trim() !== "";

// Validate Amount
if (hasValue(sourceData.Amount)) {
  const amount = parseFloat(sourceData.Amount);
  if (isNaN(amount) || amount <= 0) {
    errors.push("Amount must be a positive number");
  } else if (amount > 1000000000) {
    errors.push("Amount is too large (max: 1 billion)");
  } else {
    updates.amount = amount;
  }
}

// Validate Currency
if (hasValue(sourceData.Currency)) {
  const currency = sourceData.Currency.trim().toUpperCase();
  if (currency.length < 2 || currency.length > 5) {
    errors.push("Currency code must be 2-5 characters (e.g., IDR, USD)");
  } else {
    updates.currency = currency;
  }
}

// Validate Merchant
if (hasValue(sourceData.Merchant)) {
  const merchant = sourceData.Merchant.trim();
  if (merchant.length < 1) {
    errors.push("Merchant name cannot be empty");
  } else if (merchant.length > 100) {
    errors.push("Merchant name too long (max: 100 characters)");
  } else {
    updates.merchant = merchant;
  }
}

// Validate Bank
if (hasValue(sourceData.Bank)) {
  const bank = sourceData.Bank.trim();
  if (bank.length < 1) {
    errors.push("Bank name cannot be empty");
  } else if (bank.length > 50) {
    errors.push("Bank name too long (max: 50 characters)");
  } else {
    updates.bank = bank;
  }
}

// Validate Date
if (hasValue(sourceData.Date)) {
  if (!DATE_REGEX.test(sourceData.Date)) {
    errors.push("Invalid date format. Use YYYY-MM-DD");
  } else {
    const date = new Date(sourceData.Date);
    const now = new Date();
    const oneYearAgo = new Date(
      now.getFullYear() - 1,
      now.getMonth(),
      now.getDate(),
    );
    const oneYearFromNow = new Date(
      now.getFullYear() + 1,
      now.getMonth(),
      now.getDate(),
    );

    if (date < oneYearAgo) {
      warnings.push("Date is more than 1 year ago");
    } else if (date > oneYearFromNow) {
      errors.push("Date cannot be more than 1 year in the future");
    }

    updates.date = sourceData.Date;
  }
}

// Validate Time
if (hasValue(sourceData.Time)) {
  if (!TIME_REGEX.test(sourceData.Time)) {
    errors.push("Invalid time format. Use HH:MM (e.g., 14:30)");
  } else {
    const [hour, minute] = sourceData.Time.split(":").map(Number);
    if (hour < 0 || hour > 23) {
      errors.push("Hour must be between 00-23");
    } else if (minute < 0 || minute > 59) {
      errors.push("Minute must be between 00-59");
    } else {
      updates.time = sourceData.Time;
    }
  }
}

// Check if any updates were made
const hasUpdates = Object.keys(updates).length > 0;

if (!hasUpdates && errors.length === 0) {
  warnings.push("No fields were updated");
}

return {
  isValid: errors.length === 0,
  hasUpdates,
  updates,
  errors,
  warnings,
  originalData: sourceData,
};

