const callbackData = $json.originalCallbackData;
const parts = callbackData.split('_');
const newCategory = parts[2]; // "food", "transportation", etc.

// Validate category
const VALID_CATEGORIES = ['food', 'accommodation', 'entertainment', 'transportation', 'shopping', 'utilities', 'healthcare', 'other'];

if (!VALID_CATEGORIES.includes(newCategory)) {
