// Current Expenses
const currentExpense = $('Format Data for Parsing').first().json

// Get current date formats for filtering
const currentMonth = $now.toFormat('yyyy-MM');
const today = $now.toFormat('yyyy-MM-dd');

// Get all input data safely
const allData = $input.all() || [];

// Filter expenses by time period AND exclude internal transfers
const monthlyExpenses = allData.filter(item => {
  const date = item?.json?.date;
  const isTransfer = item?.json?.isInternalTransfer === true;
  return date && typeof date === 'string' && date.startsWith(currentMonth) && !isTransfer;
});

const dailyExpenses = allData.filter(item => {
  const date = item?.json?.date;
  const isTransfer = item?.json?.isInternalTransfer === true;
  return date && date === today && !isTransfer;
});

// Calculate totals by currency (expenses only)
const dailyTotals = {};
const monthlyTotals = {};

// Process daily expenses (excluding transfers)
dailyExpenses.forEach(expense => {
  const currency = expense?.json?.currency;
  const amount = parseFloat(String(expense?.json?.amount || 0));

  if (currency && !isNaN(amount)) {
    dailyTotals[currency] = (dailyTotals[currency] || 0) + amount;
  }
});

// Process monthly expenses (excluding transfers)
monthlyExpenses.forEach(expense => {
  const currency = expense?.json?.currency;
  const amount = parseFloat(String(expense?.json?.amount || 0));

  if (currency && !isNaN(amount)) {
    monthlyTotals[currency] = (monthlyTotals[currency] || 0) + amount;
  }
});

// Format for display (expenses only)
const dailyDisplay = Object.entries(dailyTotals)
  .map(([currency, amount]) => `${currency} ${amount.toLocaleString()}`)
  .join(' + ') || 'No expenses today';

const monthlyDisplay = Object.entries(monthlyTotals)
  .map(([currency, amount]) => `${currency} ${amount.toLocaleString()}`)
  .join(' + ') || 'No expenses this month';

// Count transfers for additional info
const dailyTransfers = allData.filter(item => {
  const date = item?.json?.date;
  const isTransfer = item?.json?.isInternalTransfer === true;
  return date && date === today && isTransfer;
}).length;

const monthlyTransfers = allData.filter(item => {
  const date = item?.json?.date;
  const isTransfer = item?.json?.isInternalTransfer === true;
  return date && typeof date === 'string' && date.startsWith(currentMonth) && isTransfer;
}).length;

return {
  currentExpense,
  dailyTotals,
  monthlyTotals,
  dailyDisplay,
  monthlyDisplay,
  dailyTransfers,
  monthlyTransfers
};

